pm.globals.set("availreportdetails", (function availreportdetails() {
    pm.test("Status code is 200", function() {
        pm.expect(pm.response.code).to.eql(200);
    });

    var response = pm.response.json();
    var nr = response.data || {};

    pm.test("availability_details", function() {
        pm.expect(nr).to.have.property("availability_details");
        pm.expect(nr.availability_details).to.not.be.null;
    });

    // Summary details validation
    if (nr.hasOwnProperty("summary_details")) {
        var summary = nr.summary_details;
        pm.test("down_count", function() { pm.expect(summary).to.have.property("down_count").that.is.not.null; });
        pm.test("downtime_duration", function() { pm.expect(summary).to.have.property("downtime_duration").that.is.not.null; });
        pm.test("availability_percentage", function() { pm.expect(summary).to.have.property("availability_percentage").that.is.not.null; });
        pm.test("mtbf", function() { pm.expect(summary).to.have.property("mtbf").that.is.not.null; });
        pm.test("unmanaged_duration", function() { pm.expect(summary).to.have.property("unmanaged_duration").that.is.not.null; });
        pm.test("alarm_count", function() { pm.expect(summary).to.have.property("alarm_count").that.is.not.null; });
        pm.test("mttr", function() { pm.expect(summary).to.have.property("mttr").that.is.not.null; });
        pm.test("maintenance_percentage", function() { pm.expect(summary).to.have.property("maintenance_percentage").that.is.not.null; });
        pm.test("maintenance_duration", function() { pm.expect(summary).to.have.property("maintenance_duration").that.is.not.null; });
        pm.test("availability_duration", function() { pm.expect(summary).to.have.property("availability_duration").that.is.not.null; });
        pm.test("unmanaged_percentage", function() { pm.expect(summary).to.have.property("unmanaged_percentage").that.is.not.null; });
        pm.test("downtime_percentage", function() { pm.expect(summary).to.have.property("downtime_percentage").that.is.not.null; });
    } else {
        pm.test("summary_details is missing", function() {
            pm.expect(nr).to.have.property("summary_details");
        });
    }

    // Location wise availability details
    if (nr.hasOwnProperty("availability_details") && nr.availability_details.length > 0) {
        nr.availability_details.forEach(function(detail) {
            pm.test("down_count", function() { pm.expect(detail).to.have.property("down_count").that.is.not.null; });
            pm.test("name", function() { pm.expect(detail).to.have.property("name").that.is.not.null; });
            pm.test("downtime_duration", function() { pm.expect(detail).to.have.property("downtime_duration").that.is.not.null; });
            pm.test("availability_percentage", function() { pm.expect(detail).to.have.property("availability_percentage").that.is.not.null; });
            pm.test("mtbf", function() { pm.expect(detail).to.have.property("mtbf").that.is.not.null; });
            pm.test("unmanaged_duration", function() { pm.expect(detail).to.have.property("unmanaged_duration").that.is.not.null; });
            pm.test("location_id", function() { pm.expect(detail).to.have.property("location_id").that.is.not.null; });
            pm.test("mttr", function() { pm.expect(detail).to.have.property("mttr").that.is.not.null; });
            pm.test("maintenance_percentage", function() { pm.expect(detail).to.have.property("maintenance_percentage").that.is.not.null; });
            pm.test("maintenance_duration", function() { pm.expect(detail).to.have.property("maintenance_duration").that.is.not.null; });
            pm.test("availability_duration", function() { pm.expect(detail).to.have.property("availability_duration").that.is.not.null; });
            pm.test("monitor_id", function() { pm.expect(detail).to.have.property("monitor_id").that.is.not.null; });
            pm.test("unmanaged_percentage", function() { pm.expect(detail).to.have.property("unmanaged_percentage").that.is.not.null; });
            pm.test("downtime_percentage", function() { pm.expect(detail).to.have.property("downtime_percentage").that.is.not.null; });
        });
    }

    // Chart data validation
    if (nr.hasOwnProperty("charts")) {
        nr.charts.forEach(function(chart) {
            pm.test("name", function() { pm.expect(chart).to.have.property("name"); });
            pm.test("key", function() { pm.expect(chart).to.have.property("key"); });
            pm.test("data", function() { pm.expect(chart).to.have.property("data").that.is.an('array'); });
            pm.test("charts", function() { pm.expect(nr).to.have.property("charts"); });
        });
    } else {
        pm.test("charts node is missing", function() {
            pm.expect(nr).to.have.property("charts");
        });
    }

    // Info data validation
    if (nr.hasOwnProperty("info")) {
        var info = nr.info;
        pm.test("resource_name", function() { pm.expect(info).to.have.property("resource_name"); });
        pm.test("end_time", function() { pm.expect(info).to.have.property("end_time"); });
        pm.test("resource_type_name", function() { pm.expect(info).to.have.property("resource_type_name"); });
        pm.test("report_name", function() { pm.expect(info).to.have.property("report_name"); });
        pm.test("period_name", function() { pm.expect(info).to.have.property("period_name"); });
        pm.test("formatted_start_time", function() { pm.expect(info).to.have.property("formatted_start_time"); });
        pm.test("generated_time", function() { pm.expect(info).to.have.property("generated_time"); });
        pm.test("formatted_end_time", function() { pm.expect(info).to.have.property("formatted_end_time"); });
        pm.test("segment_type", function() { pm.expect(info).to.have.property("segment_type"); });
        pm.test("start_time", function() { pm.expect(info).to.have.property("start_time"); });
        pm.test("resource_type", function() { pm.expect(info).to.have.property("resource_type"); });
        pm.test("period", function() { pm.expect(info).to.have.property("period"); });
        pm.test("formatted_generated_time", function() { pm.expect(info).to.have.property("formatted_generated_time"); });
        pm.test("report_type", function() { pm.expect(info).to.have.property("report_type"); });
        pm.test("segment_type_name", function() { pm.expect(info).to.have.property("segment_type_name"); });
        if (info.hasOwnProperty("resource_tags")) {
            pm.test("resource_tags", function() { pm.expect(info).to.have.property("resource_tags"); });
        }
    } else {
        pm.test("info is missing", function() {
            pm.expect(nr).to.have.property("info");
        });
    }

    // Outage data validation
    if (nr.hasOwnProperty("outage_details")) {
        if (nr.summary_details.down_count === 0) {
            pm.test("outage_details", function() {
                pm.expect(nr.outage_details).to.be.an('array').that.is.empty;
            });
        } else {
            nr.outage_details.forEach(function(outage) {
                pm.test("outages", function() { pm.expect(outage).to.have.property("outages"); });
                pm.test("monitor_id", function() { pm.expect(outage).to.have.property("monitor_id"); });
                if (outage.outages !== null) {
                    outage.outages.forEach(function(od) {
                        pm.test("outage_id", function() { pm.expect(od).to.have.property("outage_id").that.is.not.null; });
                        pm.test("end_time", function() { pm.expect(od).to.have.property("end_time").that.is.not.null; });
                        pm.test("type", function() { pm.expect(od).to.have.property("type").that.is.not.null; });
                        pm.test("start_time", function() { pm.expect(od).to.have.property("start_time").that.is.not.null; });
                        pm.test("duration", function() { pm.expect(od).to.have.property("duration").that.is.not.null; });
                    });
                } else {
                    pm.test("outages node is missing", function() {
                        pm.expect(outage).to.have.property("outages");
                    });
                }
            });
        }
    } else {
        pm.test("outage_details node is missing", function() {
            pm.expect(nr).to.have.property("outage_details");
        });
    }

    // Profile details
    if (nr.hasOwnProperty("profile_details")) {
        var profile = nr.profile_details;
        pm.test("profile_details", function() { pm.expect(profile).to.be.an('object'); });
        pm.test("threshold_profiles", function() { pm.expect(profile).to.have.property("threshold_profiles").that.is.not.null; });
        pm.test("threshold_profile_details", function() { pm.expect(profile).to.have.property("threshold_profile_details").that.is.not.null; });
        pm.test("location_profiles", function() { pm.expect(profile).to.have.property("location_profiles").that.is.not.null; });
    } else {
        pm.test("profile_details node is missing", function() {
            pm.expect(nr).to.have.property("profile_details");
        });
    }
}) + ' availreportdetails()');

