var response = pm.response.json();
var monitorList = response.data.monitors || [];
monitorList.forEach(function(monitor) {
    if (monitor.monitor_type === 'URL' && monitor.status === 1 && monitor.attribute_value !== '-') {
        pm.environment.set("getUrlMonitor", monitor.monitor_id);
    }
    if (monitor.monitor_type === 'SMTP' && monitor.status === 1 && monitor.attribute_value !== '-') {
        pm.environment.set("getSmtpMonitor", monitor.monitor_id);
    }
    if (monitor.monitor_type === 'SERVER' && (monitor.status === 1 || monitor.status === 2 || monitor.status === 7) && monitor.attribute_value !== '-') {
        pm.environment.set("getServerMonitor", monitor.monitor_id);
    }
    if (monitor.monitor_type === 'REALBROWSER' && monitor.status === 1 && monitor.attribute_value !== '-') {
        pm.globals.set("getRBMMid", monitor.monitor_id);
    }
    if (monitor.monitor_type === 'DNS' && (monitor.status === 1 || monitor.status === 2) && monitor.attribute_value !== '-') {
        pm.globals.set("getDNSMonitor", monitor.monitor_id);
    }
    if (monitor.monitor_type === 'HOMEPAGE' && (monitor.status === 1 || monitor.status === 2) && monitor.attribute_value !== '-') {
        pm.globals.set("getHOMEPAGEMonitor", monitor.monitor_id);
    }
    if (monitor.monitor_type === 'UDP' && (monitor.status === 1 || monitor.status === 2)) {
        pm.globals.set("getUDPMonitor", monitor.monitor_id);
    }
    if (monitor.monitor_type === 'PORT' && (monitor.status === 1 || monitor.status === 2) && monitor.attribute_value !== '-') {
        pm.globals.set("getPORTMonitor", monitor.monitor_id);
    }
    if (monitor.monitor_type === 'PORT-POP' && (monitor.status === 1 || monitor.status === 2) && monitor.attribute_value !== '-') {
        pm.globals.set("getPOPMonitor", monitor.monitor_id);
    }
    if (monitor.monitor_type === 'PING' && (monitor.status === 1 || monitor.status === 2) && monitor.attribute_value !== '-') {
        pm.globals.set("getPINGMonitor", monitor.monitor_id);
    }
    if (monitor.monitor_type === 'PORT-SMTP' && (monitor.status === 1 || monitor.status === 2) && monitor.attribute_value !== '-') {
        pm.globals.set("getPORT_SMTPMonitor", monitor.monitor_id);
    }
    if (monitor.monitor_type === 'FILEUPLOAD' && (monitor.status === 1 || monitor.status === 2) && monitor.attribute_value !== '-') {
        pm.globals.set("getFILEUPLOADMonitor", monitor.monitor_id);
    }
    if (monitor.monitor_type === 'GRPC' && (monitor.status === 1 || monitor.status === 2) && monitor.attribute_value !== '-') {
        pm.globals.set("getGRPCMonitor", monitor.monitor_id);
    }
});

pm.test("Status code is 200", function() {
    pm.expect(pm.response.code).to.equal(200);
});

