pm.globals.set("availreportdetailsford", function availreportdetailsford() {
    var response = pm.response.json();
    var nr = response.data || {};
    var resourcetype = nr.info?.resource_type;

    if (response.code === 0 && response.error === undefined) {
        pm.test("Status code is 200", function() {
            pm.expect(pm.response.code).to.equal(200);
        });
        pm.test("code", function() {
            pm.expect(response.code).to.equal(0);
        });
        pm.test("message", function() {
            pm.expect(response.message).to.equal("success");
        });

        // Core Presence Checks
        ["summary_details", "charts", "availability_details", "outage_details"].forEach(prop => {
            pm.test(`${prop} is present`, function() {
                pm.expect(nr).to.have.property(prop);
            });
        });

        // Profile Details Checks (resource-dependent)
        if (resourcetype === 4 || resourcetype === 1) {
            pm.test("profile_details is not present", function() {
                pm.expect(nr).to.not.have.property("profile_details");
            });
        } else {
            pm.test("profile_details is present", function() {
                pm.expect(nr).to.have.property("profile_details");
            });

            if (nr.profile_details && Object.keys(nr.profile_details).length > 0) {
                let pd = nr.profile_details;

                if (Array.isArray(pd.threshold_profiles) && pd.threshold_profiles.length > 0) {
                    let tp = pd.threshold_profiles[0];
                    pm.test("threshold_profiles[0].name is present", () => pm.expect(tp).to.have.property("name"));
                    pm.test("threshold_profiles[0].down_condition is present", () => pm.expect(tp).to.have.property("down_condition"));
                    pm.test("threshold_profiles[0].show_name is present", () => {
                        if (!tp.show_name) {
                            pm.expect(true).to.be.true;
                        } else {
                            pm.expect(tp.show_name).to.be.true;
                        }
                    });
                }

                if (pd.threshold_profile_details) {
                    pm.test("threshold_profile_details is present", () => pm.expect(pd).to.have.property("threshold_profile_details"));
                }

                if (Array.isArray(pd.location_profiles) && pd.location_profiles.length > 0) {
                    let lp = pd.location_profiles[0];
                    pm.test("location_profiles[0].locations is present", () => pm.expect(lp).to.have.property("locations"));
                    pm.test("location_profiles[0].name is present", () => pm.expect(lp).to.have.property("name"));
                }
            }
        }

        // Info Block Validations
        if (nr.info) {
            let infolength = 0, typename = "";

            if (resourcetype === 1) { infolength = 16; typename = "Group"; }
            if (resourcetype === 2) { infolength = 17; typename = "Monitor"; }
            if (resourcetype === 4) { infolength = 16; typename = "Monitor Type"; }

            if (response.hasOwnProperty("resource_tags")) infolength += 1;

            if (Object.keys(nr.info).length === infolength) {
                pm.test("info present", () => pm.expect(true).to.be.true);
                pm.test("resource_type", () => pm.expect(nr.info.resource_type).to.equal(resourcetype));
                pm.test("report_type", () => pm.expect(nr.info.report_type).to.equal(11));
                [
                    "formatted_generated_time", "period", "start_time", "generated_time", "formatted_end_time",
                    "formatted_start_time", "period_name", "report_name", "resource_name", "end_time",
                    "segment_type_name", "segment_type"
                ].forEach(prop => {
                    pm.test(`info.${prop} is present`, () => pm.expect(nr.info).to.have.property(prop));
                });

                if (resourcetype !== 4) {
                    pm.test("resource_id is present", () => pm.expect(nr.info).to.have.property("resource_id"));
                }

                pm.test("resource_type_name is correct", () => pm.expect(nr.info.resource_type_name).to.equal(typename));
            } else {
                pm.test("info attribute count mismatch", () => {
                    pm.expect(Object.keys(nr.info).length).to.equal(infolength);
                });
            }
        } else {
            pm.test("info not present", () => pm.expect(false).to.be.false);
        }
    }
