});

var response = pm.response.json();
var nr = response.data || {};
var location = pm.globals.get("getGRPCMonitorprimarylocation");

// Info data validation
if (nr.hasOwnProperty("info")) {
    [
        "resource_name", "end_time", "report_name", "period_name", "formatted_start_time",
        "generated_time", "formatted_end_time", "segment_type", "start_time", "period",
        "formatted_generated_time", "report_type", "segment_type_name", "metric_aggregation_name",
        "metric_aggregation"
    ].forEach(function (key) {
        pm.test(key, function () {
            pm.expect(nr.info[key]).to.not.be.undefined;
        });
    });

    pm.test("resource_type_name", function () {
        pm.expect(nr.info.resource_type_name).to.equal("Monitor");
    });

    pm.test("resource type", function () {
        pm.expect(nr.info.resource_type).to.equal(2);
    });

    if (nr.info.hasOwnProperty("resource_tags")) {
        pm.test("resource_tags", function () {
            pm.expect(nr.info.resource_tags).to.not.be.undefined;
        });
