pm.test("Status code is 200", () => {
    pm.expect(pm.response.code).to.equal(200);
});

const response = pm.response.json();
const data = response.data || {};
const info = data.info || {};
const tableData = data.table_data || {};
const chartData = data.chart_data || [];
const location = pm.globals.get("POPMonitorprimarylocation");

// ----------- INFO VALIDATION -----------
pm.test("Info block exists", () => pm.expect(info).to.be.an("object"));

const requiredInfoFields = [
    "resource_name", "end_time", "report_name", "period_name",
    "formatted_start_time", "generated_time", "formatted_end_time",
    "segment_type", "start_time", "period", "formatted_generated_time",
    "report_type", "segment_type_name", "metric_aggregation_name",
    "metric_aggregation"
];

requiredInfoFields.forEach(field => {
    pm.test(`info.${field} is defined`, () => {
        pm.expect(info[field]).to.not.be.undefined;
    });
});

pm.test("info.resource_type_name is 'Monitor'", () => {
    pm.expect(info.resource_type_name).to.equal("Monitor");
});

pm.test("info.resource_type is 2", () => {
    pm.expect(info.resource_type).to.equal(2);
});

if (info.resource_tags !== undefined) {
    pm.test("info.resource_tags is defined", () => {
        pm.expect(info.resource_tags).to.not.be.undefined;
    });
}

// ----------- TABLE DATA VALIDATION -----------
pm.test("table_data exists", () => {
    pm.expect(tableData).to.be.an("object");
    pm.expect(tableData[location]).to.not.be.undefined;
});

const respTime = tableData[location]?.RESPONSETIME;

if (respTime) {
    ["max", "min", "average", "unit", "average_description", "title", "chart_data"].forEach(field => {
        pm.test(`RESPONSETIME.${field} is defined`, () => {
            pm.expect(respTime[field]).to.not.be.undefined;
        });
    });
} else {
    pm.test("RESPONSETIME missing for location", () => {
        pm.expect(respTime).to.not.be.undefined;
    });
}

// ----------- CHART DATA VALIDATION -----------
pm.test("chart_data has 2+ entries", () => {
    pm.expect(chartData.length).to.be.at.least(2);
});

// -- Chart 0: ResponseTimeChart --
const rtc = chartData[0]?.ResponseTimeChart?.[0]?.[location];
if (rtc) {
    ["max", "min", "label", "average", "95_percentile", "chart_data"].forEach(field => {
        pm.test(`ResponseTimeChart.${field} is defined`, () => {
            pm.expect(rtc[field]).to.not.be.undefined;
        });
    });
}

// -- Chart 1: LocationResponseTimeChart --
const locRTCArray = chartData[1]?.LocationResponseTimeChart || [];
let validEntry = locRTCArray.find(entry => entry[location]);

if (validEntry) {
    const locRTC = validEntry[location];
    ["max", "min", "label", "average", "95_percentile", "chart_data"].forEach(field => {
        pm.test(`LocationResponseTimeChart.${field} is defined`, () => {
            pm.expect(locRTC[field]).to.not.be.undefined;
        });
    });
} else {
    pm.test("No valid entry found in LocationResponseTimeChart", () => {
        pm.expect(validEntry).to.not.be.undefined;
    });
}

