// Check if the response code is 0 (success)
if (jsonData.code === 0) {
    if (jsonData.error === undefined) {
        // Basic response validations
        tests["Status code is 200"] = responseCode.code === 200;
        tests["Response code is 0"] = jsonData.code === 0;
        tests["Response message is success"] = jsonData.message === "success";

        // Validate the data object
        if (jsonData.data) {
            var data = jsonData.data;

            // Validate info object
            if (data.info) {
                var info = data.info;

                tests["Info period is not null"] = info.period !== null;
                tests["Info resource_type_name is not null"] = info.resource_type_name !== null;
                tests["Info resource_type is not null"] = info.resource_type !== null;
                tests["Info end_time is not null"] = info.end_time !== null;
                tests["Info period_name is not null"] = info.period_name !== null;
                tests["Info formatted_start_time is not null"] = info.formatted_start_time !== null;
                tests["Info report_type is not null"] = info.report_type !== null;
                tests["Info formatted_generated_time is not null"] = info.formatted_generated_time !== null;
                tests["Info formatted_end_time is not null"] = info.formatted_end_time !== null;
                tests["Info generated_time is not null"] = info.generated_time !== null;
                tests["Info start_time is not null"] = info.start_time !== null;
                tests["Info resource_id is not null"] = info.resource_id !== null;
                tests["Info resource_name is not null"] = info.resource_name !== null;
                tests["Info monitor_type is not null"] = info.monitor_type !== null;
            } else {
                tests["Info object is missing"] = false;
            }

            // Validate report_details array
            if (data.report_details && data.report_details.length > 0) {
                tests["Report details array is not empty"] = data.report_details.length > 0;

                // Iterate through report_details and validate each report
                data.report_details.forEach(function (report, index) {
                    tests[`Report ${index + 1} monitor_id is not null`] = report.monitor_id !== null;
                    tests[`Report ${index + 1} display_name is not null`] = report.display_name !== null;

                    // Validate sequence_location_data array
                    if (report.sequence_location_data && report.sequence_location_data.length > 0) {
                        tests[`Report ${index + 1} sequence_location_data is not empty`] = report.sequence_location_data.length > 0;

                        report.sequence_location_data.forEach(function (location, locIndex) {
                            tests[`Report ${index + 1} Location ${locIndex + 1} id is not null`] = location.id !== null;
                            tests[`Report ${index + 1} Location ${locIndex + 1} label is not null`] = location.label !== null;

                            // Validate values array
                            if (location.values && location.values.length > 0) {
                                tests[`Report ${index + 1} Location ${locIndex + 1} values array is not empty`] = location.values.length > 0;

                                location.values.forEach(function (value, valueIndex) {
                                    tests[`Report ${index + 1} Location ${locIndex + 1} Value ${valueIndex + 1} is not null`] = value !== null;
                                });
                            } else {
                                tests[`Report ${index + 1} Location ${locIndex + 1} values array is empty`] = false;
                            }
                        });
                    } else {
                        tests[`Report ${index + 1} sequence_location_data is empty`] = false;
                    }

                    // Validate step_names object
                    if (report.step_names) {
                        Object.keys(report.step_names).forEach(function (stepId) {
                            tests[`Report ${index + 1} Step name for ID ${stepId} is not null`] = report.step_names[stepId] !== null;
                        });
                    } else {
                        tests[`Report ${index + 1} step_names object is missing`] = false;
                    }
                });
            } else {
                tests["Report details array is empty"] = false;
            }
        } else {
            tests["Data object is missing"] = false;
        }
    }
} else {
    // If the response code is not 0, mark the test as failed
    tests["Response code is not 0"] = false;
