pm.test("Status code is 200", function() {
    pm.expect(pm.response.code).to.equal(200);
});

pm.test("code", function() {
    pm.expect(response.code).to.equal(0);
});

pm.test("message", function() {
    pm.expect(response.message).to.equal("success");
});

if (response.code === 0) {
    if (!response.hasOwnProperty('error')) {
        const nr = response.data || {};
        if (nr.availabilityslareport) {
            if (nr.availabilityslareport.length > 0) {
                const firstReport = nr.availabilityslareport[0];
                if (Object.keys(firstReport).length === 16) {
                    for (let i = 0; i < nr.availabilityslareport.length; i++) {
