                            pm.expect(report.totaldowntime).to.not.be.null;
                        });
                        pm.test(" businessid " + i, function() {
                            pm.expect(report.businessid).to.not.be.null;
                        });
                        pm.test(" monitor_name " + i, function() {
                            pm.expect(report.monitor_name).to.not.be.null;
                        });
                        pm.test(" targetname " + i, function() {
                            pm.expect(report.targetname).to.not.be.null;
                        });
                        pm.test(" colour " + i, function() {
                            pm.expect(report.colour).to.not.be.null;
                        });
                        pm.test(" overalltargets " + i, function() {
                            pm.expect(report.overalltargets).to.not.be.null;
                        });
                        if (report.overalltargets !== null && report.overalltargets.length > 0) {
                            const target = report.overalltargets[0];
                            pm.test(" overalltargets operatorkey" + i, function() {
                                pm.expect(target.operatorkey).to.not.be.null;
                            });
                            pm.test(" overalltargets operator" + i, function() {
                                pm.expect(target.operator).to.not.be.null;
                            });
                            pm.test(" overalltargets name" + i, function() {
                                pm.expect(target.name).to.not.be.null;
                            });
                            pm.test(" overalltargets target" + i, function() {
                                pm.expect(target.target).to.not.be.null;
                            });
                            pm.test(" overalltargets colour" + i, function() {
                                pm.expect(target.colour).to.not.be.null;
                            });
                        }
                    }
                } else {
                    if (Object.keys(firstReport).length > 16) {
                        pm.test("availability SLA  new attribute added ... ", function() {
                            pm.expect(false).to.be.true;
                        });
                    } else {
                        pm.test("availability SLA  attribute removed ... ", function() {
                            pm.expect(false).to.be.true;
                        });
                    }
                }
            } else {
                pm.test("no availability SLA  ... ", function() {
                    pm.expect(false).to.be.true;
                });
            }
        } else {
            pm.test("no availability SLA  ... ", function() {
                pm.expect(false).to.be.true;
            });
        }
    } else {
        pm.test("availability SLA error", function() {
            pm.expect(false).to.be.true;
        });
    }
} else {
    pm.test("availability SLA error", function() {
        pm.expect(false).to.be.true;
    });
}

